function [bestEpsilon, bestF1] = selectThreshold(yval, pval)% SELECTTHRESHOLD Find the best threshold (epsilon) to use for selecting% outliers%   [bestEpsilon bestF1] = SELECTTHRESHOLD(yval, pval) finds the best%   threshold (epsilon) to use for selecting outliers based on the results from a%   validation set (pval) and the ground truth (yval).%bestEpsilon = 0;bestF1 = 0;F1 = 0;% m is the number of examplesstepsize = (max(pval) - min(pval)) / 1000;for epsilon = min(pval):stepsize:max(pval)        predictions = (pval < epsilon); % boolean vector m x 1, every 1 marks an error.    total_predicted_neg = sum(predictions == 0);     total_predicted_pos = sum(predictions == 1);    actual_positives = sum(yval == 1); % total number of true errors in y        fn = sum(predictions == 0 & yval == 1); % false negatives    tp = sum(predictions == 1 & yval == 1); % true positives    fp = sum(predictions == 1 & yval == 0); % false positives        recall = tp / (tp + fn);    precision = tp / (tp + fp);        F1 = (2 * precision * recall) / (precision + recall);    if F1 > bestF1       bestF1 = F1;       bestEpsilon = epsilon;    endendend